import React from 'react';
import './BookDetails.css';

const BookDetails = (props) => {
  // Sample book details for demonstration
  const book = {
    title: 'Sample Book Title',
    author: 'Sample Author',
    genre: 'Sample Genre',
    description:
      'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Nulla vel justo vel justo varius volutpat. Integer sed faucibus dolor.',
    coverImage: 'book-cover.jpg', // Replace with the actual cover image
  };

  return (
    <div>
      <h1>Book Details</h1>
      <div className="book-details">
        <img src={book.coverImage} alt="Book Cover" className="book-cover" />
        <div className="book-info">
          <h2>{book.title}</h2>
          <p>Author: {book.author}</p>
          <p>Genre: {book.genre}</p>
          <p>Description: {book.description}</p>
        </div>
      </div>
    </div>
  );
};

export default BookDetails;
